import React, { useState } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { registerUser } from '../api'; // Adjust the path as needed

const RegisterPage = () => {
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });
  const [error, setError] = useState(null); // State to hold error message
  const [isRegistered, setIsRegistered] = useState(false); // State to track registration success

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await registerUser(formData); // Assuming registerUser sends a request to the backend
      console.log('Registration successful');
      setIsRegistered(true); // Update state to indicate successful registration
    } catch (error) {
      console.error('Registration error:', error);
      setError('Registration failed'); // Update error state if registration fails
    }
  };

  if (isRegistered) {
    return <Redirect to="/success" />; // Redirect to success page upon successful registration
  }

  return (
    <div className="auth-container">
      <h2>Register</h2>
      {error && <div className="error-message">{error}</div>} {/* Display error message */}
      <form onSubmit={handleSubmit} className="auth-form">
        <div className="form-group">
          <label>Username</label>
          <input type="text" name="username" value={formData.username} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input type="password" name="password" value={formData.password} onChange={handleChange} required />
        </div>
        <button type="submit" className="auth-button">Register</button>
      </form>
      <p>
        Already have an account? <Link to="/login">Login</Link>
      </p>
    </div>
  );
};

export default RegisterPage;
